package findthedifference

import "testing"

func TestFindTheDifference(t *testing.T) {
	var a, b string
	var ret, expect byte

	a = "abcd"
	b = "abcde"
	expect = 'e'
	ret = findTheDifference(a, b)
	if ret != expect {
		t.Errorf("Get %s, Expect %s", string(ret), string(expect))
	}

	a = "abcd"
	b = "dbamc"
	expect = 'm'
	ret = findTheDifference(a, b)
	if ret != expect {
		t.Errorf("Get %s, Expect %s", string(ret), string(expect))
	}

	a = "a"
	b = "aa"
	expect = 'a'
	ret = findTheDifference(a, b)
	if ret != expect {
		t.Errorf("Get %s, Expect %s", string(ret), string(expect))
	}

	a = ""
	b = ""
	expect = 't'
	ret = findTheDifference(a, b)
	if ret != expect {
		t.Errorf("Get %s, Expect %s", string(ret), string(expect))
	}
}
